  Ayat(
    id: 3472,
    nomorSurah: 31,
    nomorAyat: 3,
    arabic: "هُدًى وَّرَحْمَةً لِّلْمُحْسِنِيْنَۙ",
    transliterasi: "hudaw wa raḥmatal lil-muḥsinīn",
    rukuk: 11,
    indonesianTranslate: "menjadi petunjuk dan rahmat bagi orang-orang yang berbuat kebaikan."
  ),
  Ayat(
