  ),
  Ayat(
    id: 4473,
    nomorSurah: 44,
    nomorAyat: 59,
    arabic: "فَارْتَقِبْ اِنَّهُمْ مُّرْتَقِبُوْنَࣖ",
    transliterasi: "fartaqib innahum murtaqibụn",
